mapper.select:
SELECT id, bid, (SELECT mcl.label_name FROM maa_conf_label mcl WHERE mcl.label_code = 'IPO_CAPITAL_MARKET' AND mcl.label_value = market_type) AS marketType,
 company_code AS companyCode, company_name AS companyName, list_time AS listTime, delist_time AS delistTime FROM ipo_other_market_info
WHERE bid = #{bid}

SELECT t.*, mcl.label_name AS intermediaryTypeName FROM ( SELECT iio.intermediary_type AS intermediaryType, iio.org_type AS orgType, iio.org_name AS orgName, iio.org_code AS orgCode, iio.valid_flag AS validFlag, iio.represent_person AS representPerson, iio.agent_person AS agentPerson, iio.assist_person AS assistPerson, CASE WHEN iio.intermediary_type = '1' THEN 'IPO_SPONSOR_TYPE' WHEN iio.intermediary_type = '2' THEN 'IPO_SECURITY_TYPE' WHEN iio.intermediary_type = '3' THEN 'IPO_LAWYER_TYPE' WHEN iio.intermediary_type = '4' THEN 'IPO_ACCOUNT_TYPE' WHEN iio.intermediary_type = '5' THEN 'IPO_ASSET_VALUATION_TYPE' ELSE '' END AS labelCode FROM ipo_intermediary_org iio WHERE iio.bid = #{bid}
AND iio.valid_flag = #{validFlag}
) t JOIN maa_conf_label mcl ON t.labelCode = mcl.label_code AND t.orgType = mcl.label_value ORDER BY t.intermediaryType
