mapper.select:
SELECT t1.bid, t4.param_name, t4.sys_update, t1.tree_type_code, t1.process_type_code, t2.label_name, t1.pro_sort, t1.id, t1.relation_file_id, t3.title, date_format(t1.process_time,'%Y-%m-%d') process_time, t1.table_sort, t1.`status`, t3.suffix, t1.iec_result, t1.relation_file_title FROM ipo_process t1 LEFT JOIN maa_conf_label t2 ON t2.label_value = t1.process_type_code AND t2.label_code IN ( 'SUPPORT_PROGRESS', 'DECLARE_PROGRESS', 'PUBLIC_PROGRESS' ) LEFT JOIN ipo_file t3 ON t3.id = t1.relation_file_id left join ipo_case t4 on t4.id = t1.bid
t1.bid = #{bid}
ORDER BY t1.pro_sort desc,t1.table_sort
