mapper.select:
SELECT core_fi AS core, dev_fi AS dev, count_fi AS people, category_date_fi AS indexDate FROM ipo_technology WHERE bid = #{id}
UNION
SELECT core_se AS core, dev_se AS dev, count_se AS people, category_date_se AS indexDate FROM ipo_technology WHERE bid =  #{id}

SELECT (case when DATE_FORMAT(now(),'%Y-%m-%d') >= CONCAT(year(now()),'-04-01') then year(NOW()) ELSE year(NOW())-1 END) AS orgYear, (SELECT SUM(1) FROM ipo_case t,case_common_status_info t1 WHERE t.id = t1.case_id AND t.ipo_plate = #{ipoPlate} AND t1.release_flag = '1' AND EXISTS(SELECT 1 FROM ipo_intermediary_org m WHERE m.bid = t.id AND m.valid_flag = '1' AND m.org_code = #{orgCode}
AND m.org_type = '01'
AND m.intermediary_type = #{intermediaryType}) AND (SELECT year(m.process_time) FROM ipo_process m WHERE m.bid = t.id AND m.process_type_code in ('04','31') AND m.table_sort = '1' ORDER BY m.pro_sort DESC LIMIT 1) = (case when DATE_FORMAT(now(),'%Y-%m-%d') >= CONCAT(year(now()),'-04-01') then year(NOW()) ELSE year(NOW())-1 END)) AS businessVolume, (SELECT SUM(1) FROM ipo_case t,case_common_status_info t1 WHERE t.id = t1.case_id AND t.ipo_plate = #{ipoPlate} AND t1.release_flag = '1' AND EXISTS(SELECT 1 FROM ipo_intermediary_org m WHERE m.bid = t.id AND m.valid_flag = '1' AND m.org_code = #{orgCode}
AND m.org_type = '01'
AND m.intermediary_type = #{intermediaryType}) AND EXISTS (SELECT 1 FROM ipo_process m WHERE m.bid = t.id AND m.process_type_code = '35' AND m.iec_result = '05') AND (SELECT year(m.process_time) FROM ipo_process m WHERE m.bid = t.id AND m.process_type_code in ('04','31') AND m.table_sort = '1' ORDER BY m.pro_sort DESC LIMIT 1) = (case when DATE_FORMAT(now(),'%Y-%m-%d') >= CONCAT(year(now()),'-04-01') then year(NOW()) ELSE year(NOW())-1 END)) AS passing, (SELECT SUM(1) FROM ipo_case t,case_common_status_info t1 WHERE t.id = t1.case_id AND t.ipo_plate = #{ipoPlate} AND t1.release_flag = '1' AND EXISTS(SELECT 1 FROM ipo_intermediary_org m WHERE m.bid = t.id AND m.valid_flag = '1' AND m.org_code = #{orgCode}
AND m.org_type = '01'
AND m.intermediary_type = #{intermediaryType}) AND EXISTS (SELECT 1 FROM ipo_process m WHERE m.bid = t.id AND m.process_type_code = '35' AND m.iec_result != '09') AND (SELECT year(m.process_time) FROM ipo_process m WHERE m.bid = t.id AND m.process_type_code in ('04','31') AND m.table_sort = '1' ORDER BY m.pro_sort DESC LIMIT 1) = (case when DATE_FORMAT(now(),'%Y-%m-%d') >= CONCAT(year(now()),'-04-01') then year(NOW()) ELSE year(NOW())-1 END)) AS passingWait
