mapper.select:
SELECT i.app_company as appCompany, i.regist_addr as registAddr, i.belong_trade as belongTrade, (select s.code_name from sa_code s where s.code_value = i.quasi_listed_land and s.code_no = "QUASI_LISTED_TYPE") as quasiListedLand, i.recommend_organization as recommendOrganization, i.accountant_office as accountantOffice, i.law_firm as lawFirm, (select s.code_name from sa_code s where s.code_value = i.approve_status and s.code_no = "IPO_APPROVE_STATUS") as approveStatus, i.hased_random_inspection as hasedRandomInspection FROM ipo_data_info i WHERE
i.recommend_organization = #{label}
AND i.quasi_listed_land = #{quasiListedLand}
AND i.regist_addr IN
#{area}
AND i.belong_trade IN
#{industry}
AND i.grab_update_time = #{lastUpadteTime}

SELECT i.app_company as appCompany, i.regist_addr as registAddr, i.belong_trade as belongTrade, (select s.code_name from sa_code s where s.code_value = i.quasi_listed_land and s.code_no = "QUASI_LISTED_TYPE") as quasiListedLand, i.recommend_organization as recommendOrganization, i.accountant_office as accountantOffice, i.law_firm as lawFirm, (select s.code_name from sa_code s where s.code_value = i.approve_status and s.code_no = "IPO_APPROVE_STATUS") as approveStatus, i.hased_random_inspection as hasedRandomInspection FROM ipo_data_info i WHERE
i.law_firm = #{label}
AND i.quasi_listed_land = #{quasiListedLand}
AND i.regist_addr IN
#{area}
AND i.belong_trade IN
#{industry}
AND i.grab_update_time = #{lastUpadteTime}

