SELECT
a.label,a.szbCount,a.hzbCount,a.zxbCount,a.cybCount,a.kcCount,a.belongTrade,a.registAddr,
(a.szbCount+a.hzbCount+a.zxbCount+a.cybCount+a.kcCount) AS totalCount,
ROUND((a.szbCount+a.hzbCount+a.zxbCount+a.cybCount+a.kcCount)/
(
SELECT COUNT(1)
FROM ipo_data_info i
WHERE i.quasi_listed_land IN ('00','01','02','03','04') AND
i.grab_update_time = #{lastUpadteTime}
)*100, 2) AS percent
FROM (
SELECT
cro.recommend_organization AS label,
idi.belong_trade as belongTrade,
idi.regist_addr as registAddr,
(
SELECT COUNT(1)
FROM ipo_data_info i, company_recommend_organization cro1
WHERE  i.id = cro1.ipo_data_id
AND cro1.recommend_organization = cro.recommend_organization
AND i.grab_update_time = #{lastUpadteTime}
AND i.quasi_listed_land = '01'

    AND i.regist_addr IN

        #{area}



    AND i.belong_trade IN

        #{industry}


) AS szbCount,
(
SELECT COUNT(1)
FROM ipo_data_info i, company_recommend_organization cro1
WHERE i.id = cro1.ipo_data_id
AND cro1.recommend_organization = cro.recommend_organization
AND  i.grab_update_time = #{lastUpadteTime}
AND i.quasi_listed_land = '00'

    AND i.regist_addr IN

        #{area}



    AND i.belong_trade IN

        #{industry}


) AS hzbCount,
(
SELECT COUNT(1)
FROM ipo_data_info i, company_recommend_organization cro1
WHERE i.id = cro1.ipo_data_id
AND cro1.recommend_organization = cro.recommend_organization
AND  i.grab_update_time = #{lastUpadteTime}
AND i.quasi_listed_land = '02'

    AND i.regist_addr IN

        #{area}



    AND i.belong_trade IN

        #{industry}


) AS zxbCount,
(
SELECT COUNT(1)
FROM ipo_data_info i, company_recommend_organization cro1
WHERE i.id = cro1.ipo_data_id
AND cro1.recommend_organization = cro.recommend_organization
AND  i.grab_update_time = #{lastUpadteTime}
AND i.quasi_listed_land = '03'

    AND i.regist_addr IN

        #{area}



    AND i.belong_trade IN

        #{industry}


) AS cybCount,

(
SELECT COUNT(1)
FROM ipo_data_info i, company_recommend_organization cro1
WHERE i.id = cro1.ipo_data_id
AND cro1.recommend_organization = cro.recommend_organization
AND  i.grab_update_time = #{lastUpadteTime}
AND i.quasi_listed_land = '04'

    AND i.regist_addr IN

        #{area}



    AND i.belong_trade IN

        #{industry}


) AS kcCount

FROM ipo_data_info idi left join  company_recommend_organization  cro on idi.id = cro.ipo_data_id
WHERE idi.recommend_organization IS NOT NULL AND idi.grab_update_time = #{lastUpadteTime}

    AND idi.regist_addr IN

        #{area}



    AND idi.belong_trade IN

        #{industry}


GROUP BY cro.recommend_organization
) a

